tables.table_schema:
CREATE TABLE performance_data (
    id SERIAL PRIMARY KEY,
    date DATE,
    metric_name VARCHAR(50),
    value FLOAT
);

CREATE TABLE historical_performance_data (
    id INT PRIMARY KEY,
    date DATE,
    category VARCHAR,
    performance_metric INT
);

CREATE TABLE trends_and_patterns (
    id INT PRIMARY KEY,
    date DATE,
    trend VARCHAR,
    pattern TEXT
);

CREATE TABLE HistoricalPerformanceData (
    id SERIAL PRIMARY KEY,
    date DATE,
    performance_metric VARCHAR(50),
    value DECIMAL
);

CREATE TABLE PerformanceTrends (
    trend_id SERIAL PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT
);

CREATE TABLE DataPatterns (
    pattern_id SERIAL PRIMARY KEY,
    pattern_name VARCHAR(50),
    pattern_description TEXT
);

CREATE TABLE performance_data (
    id INT PRIMARY KEY,
    date DATE,
    metric_name VARCHAR(255),
    value FLOAT
);

CREATE TABLE trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(255),
    description TEXT
);

CREATE TABLE analysis_results (
    result_id INT PRIMARY KEY,
    result_name VARCHAR(255),
    trend_id INT,
    pattern_id INT,
    FOREIGN KEY (trend_id) REFERENCES trends(trend_id),
    FOREIGN KEY (pattern_id) REFERENCES patterns(pattern_id)
);
