tables.table_schema:
CREATE TABLE performance_scorecards (
    id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    metric_value INT
);

CREATE TABLE visual_representations (
    id INT PRIMARY KEY,
    scorecard_id INT,
    image_url VARCHAR(255)
);

CREATE TABLE performance_scorecards (
    scorecard_id INT PRIMARY KEY,
    metric_name VARCHAR(50),
    metric_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE performance_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(50) UNIQUE
);

CREATE TABLE visual_representations (
    representation_id INT PRIMARY KEY,
    representation_name VARCHAR(50),
    representation_type VARCHAR(50)
);

CREATE TABLE performance_data (
    data_id INT PRIMARY KEY,
    metric_id INT,
    scorecard_id INT,
    value FLOAT,
    FOREIGN KEY (metric_id) REFERENCES performance_metrics(metric_id),
    FOREIGN KEY (scorecard_id) REFERENCES performance_scorecards(scorecard_id)
);

CREATE TABLE performance_scorecards (
    id INT PRIMARY KEY,
    metric_name VARCHAR,
    metric_value DECIMAL,
    date_measured DATE
);

CREATE TABLE departments (
    dept_id INT PRIMARY KEY,
    dept_name VARCHAR,
    manager_name VARCHAR
);

CREATE TABLE employees (
    emp_id INT PRIMARY KEY,
    emp_name VARCHAR,
    emp_dept_id INT,
    emp_role VARCHAR
);

CREATE TABLE metrics (
    metric_id INT PRIMARY KEY,
    metric_type VARCHAR,
    metric_description TEXT
);

CREATE TABLE metric_history (
    history_id INT PRIMARY KEY,
    metric_id INT,
    metric_value DECIMAL,
    measurement_date DATE
);
