tables.table_schema:
CREATE TABLE employees (
  employee_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE ReviewReminders (
  reminder_id INT PRIMARY KEY,
  employee_id INT,
  reminder_date DATE,
  reminder_message TEXT
);

CREATE TABLE PerformanceReviewReminders (
    employee_id INT,
    reminder_date DATE,
    reminder_message TEXT
);

CREATE TABLE ReviewScheduling (
    employee_id INT,
    review_date DATE,
    scheduled_by INT
);
