tables.table_schema:
CREATE TABLE certifications (
    certification_id INT PRIMARY KEY,
    employee_id INT,
    certification_name VARCHAR(50),
    expiration_date DATE
);

CREATE TABLE employee_certifications (
  employee_id INT,
  certification_name VARCHAR(255),
  certification_date DATE
);

CREATE TABLE employee_licenses (
  employee_id INT,
  license_name VARCHAR(255),
  license_expiry_date DATE
);

CREATE TABLE employee_qualifications (
  employee_id INT,
  qualification_name VARCHAR(255),
  qualification_description TEXT
);

CREATE TABLE EmployeeCertifications ( 
    employee_id INT PRIMARY KEY, 
    certification_name VARCHAR(255) NOT NULL, 
    certification_date DATE NOT NULL 
);

CREATE TABLE EmployeeLicenses ( 
    employee_id INT PRIMARY KEY, 
    license_name VARCHAR(255) NOT NULL, 
    license_expiry DATE NOT NULL 
);

CREATE TABLE EmployeeQualifications ( 
    employee_id INT PRIMARY KEY, 
    qualification_name VARCHAR(255) NOT NULL, 
    qualification_description TEXT 
);

CREATE TABLE CertificationTypes ( 
    certification_type_id INT PRIMARY KEY, 
    certification_type_name VARCHAR(255) NOT NULL 
);

CREATE TABLE LicenseTypes ( 
    license_type_id INT PRIMARY KEY, 
    license_type_name VARCHAR(255) NOT NULL 
);
