tables.table_schema:
CREATE TABLE employee_feedback (
  feedback_id INT PRIMARY KEY,
  employee_id INT,
  feedback_text TEXT,
  sentiment_score FLOAT
);

CREATE TABLE survey_data (
  survey_id INT PRIMARY KEY,
  employee_id INT,
  survey_date DATE,
  survey_response TEXT
);

CREATE TABLE survey_data (
    survey_id INT PRIMARY KEY,
    survey_name VARCHAR(100),
    survey_date DATE,
    feedback_count INT
);

CREATE TABLE feedback_sentiments ( 
    id UUID PRIMARY KEY, 
    employee_id INT, 
    feedback_text TEXT, 
    sentiment_score FLOAT 
);

CREATE TABLE employee_details ( 
    employee_id INT PRIMARY KEY, 
    name TEXT, 
    department TEXT, 
    position TEXT 
);

CREATE TABLE survey_responses ( 
    response_id UUID PRIMARY KEY, 
    employee_id INT, 
    survey_id UUID, 
    response TEXT 
);

CREATE TABLE survey_data ( 
    survey_id UUID PRIMARY KEY, 
    survey_name TEXT, 
    survey_date DATE 
);
