tables.table_schema:
CREATE TABLE competency_assessments (
    assessment_id INT PRIMARY KEY,
    employee_id INT,
    competency_name VARCHAR,
    competency_score INT
);

CREATE TABLE development_plans (
    plan_id INT PRIMARY KEY,
    employee_id INT,
    plan_description VARCHAR,
    target_completion_date DATE
);

CREATE TABLE Competencies (
  competency_id SERIAL PRIMARY KEY,
  competency_name VARCHAR(50) NOT NULL
);

CREATE TABLE Employees (
  employee_id SERIAL PRIMARY KEY,
  employee_name VARCHAR(50) NOT NULL,
  competency_id INT REFERENCES Competencies(competency_id)
);

CREATE TABLE Competencies (
  competency_id INT PRIMARY KEY,
  competency_name VARCHAR,
  competency_description TEXT
);

CREATE TABLE Employees (
  employee_id INT PRIMARY KEY,
  employee_name VARCHAR,
  employee_email VARCHAR,
  employee_department VARCHAR
);
