tables.table_schema:
CREATE TABLE EmployeeTrainingPrograms (
    employee_id INT PRIMARY KEY,
    training_program TEXT,
    certification TEXT,
    skill_development_progress TEXT
);

CREATE TABLE employee_training_programs (
  program_id INT PRIMARY KEY,
  program_name VARCHAR(255) NOT NULL,
  start_date DATE,
  end_date DATE
);

CREATE TABLE employee_certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(255) NOT NULL,
  certification_date DATE,
  employee_id INT
);

CREATE TABLE employee_skill_development (
  skill_id INT PRIMARY KEY,
  skill_name VARCHAR(255) NOT NULL,
  skill_level INT,
  employee_id INT
);

CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR,
    department VARCHAR,
    hire_date DATE
);

CREATE TABLE certifications (
    certification_id INT PRIMARY KEY,
    certification_name VARCHAR,
    certification_body VARCHAR
);

CREATE TABLE employee_skills (
    employee_id INT,
    skill_name VARCHAR,
    proficiency_level INT,
    FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);
