tables.table_schema:
CREATE TABLE employee_turnover (
    employee_id INT,
    turnover_date DATE,
    reason TEXT,
    FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);

CREATE TABLE employee_data (
  employee_id INT PRIMARY KEY,
  name VARCHAR(50),
  age INT,
  department VARCHAR(50)
);

CREATE TABLE turnover_data (
  turnover_id INT PRIMARY KEY,
  employee_id INT,
  turnover_date DATE,
  reason VARCHAR(100)
);

CREATE TABLE workforce_demographics (
  demographic_id INT PRIMARY KEY,
  department VARCHAR(50),
  average_tenure FLOAT,
  gender VARCHAR(10)
);

CREATE TABLE department ( 
 department_id INT PRIMARY KEY, 
 department_name VARCHAR(255)
);

CREATE TABLE retention ( 
 retention_id INT PRIMARY KEY, 
 employee_id INT, 
 retention_score INT
);

CREATE TABLE turnover ( 
 turnover_id INT PRIMARY KEY, 
 employee_id INT, 
 exit_reason VARCHAR(255), 
 exit_date DATE
);
