tables.table_schema:
CREATE TABLE performance_metrics (
    employee_id INT,
    metric_name VARCHAR(50),
    metric_value FLOAT,
    date_collected DATE
);

CREATE TABLE goal_setting (
    goal_id INT,
    employee_id INT,
    goal_description TEXT,
    due_date DATE
);

CREATE TABLE PerformanceMetrics (
    employee_id INT,
    metric_name VARCHAR(50),
    metric_value DECIMAL(10,2),
    timestamp TIMESTAMP
);

CREATE TABLE KPIs (
    kpi_id INT,
    kpi_name VARCHAR(50),
    target_value DECIMAL(10,2),
    achieved_value DECIMAL(10,2)
);

CREATE TABLE GoalSetting (
    goal_id INT,
    employee_id INT,
    goal_description TEXT,
    target_date DATE
);

CREATE TABLE KPIs (
  id INT PRIMARY KEY,
  kpi_name VARCHAR(50) NOT NULL,
  kpi_description TEXT,
  target_value DECIMAL
);

CREATE TABLE Goals (
  id INT PRIMARY KEY,
  employee_id INT NOT NULL,
  goal_description TEXT,
  target_completion_date DATE
);

CREATE TABLE Employees (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  department VARCHAR(50),
  hire_date DATE
);

CREATE TABLE Departments (
  id INT PRIMARY KEY,
  department_name VARCHAR(50) NOT NULL,
  manager_id INT
);
