tables.table_schema:
CREATE TABLE employee_turnover_rates (
    id INT PRIMARY KEY,
    employee_id INT,
    turnover_rate DECIMAL,
    date DATE
);

CREATE TABLE reasons_for_leaving (
    id INT PRIMARY KEY,
    employee_id INT,
    reason VARCHAR(255),
    date DATE
);

CREATE TABLE employee_turnover (
    id INT PRIMARY KEY,
    employee_id INT,
    turnover_date DATE,
    reason_for_leaving TEXT
);

CREATE TABLE retention_strategies (
    id INT PRIMARY KEY,
    strategy_name TEXT,
    improvement_areas TEXT
);

CREATE TABLE employee_ratings (
    id INT PRIMARY KEY,
    employee_id INT,
    performance_rating INT,
    feedback TEXT
);

CREATE TABLE retention_analysis (
    id INT PRIMARY KEY,
    analysis_date DATE,
    improvement_strategy TEXT,
    feedback_report TEXT
);
