  id UUID PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  description TEXT,
  category VARCHAR(50) NOT NULL
);

CREATE TABLE qualifications (
  id UUID PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  level INT NOT NULL,
  certification_id UUID REFERENCES certifications(id)
);

CREATE TABLE certifications (
    id UUID PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE qualifications (
    id UUID PRIMARY KEY,
    qualification_name VARCHAR(100) NOT NULL,
    level INT
