CREATE TABLE career_advancement (
    employee_id UUID PRIMARY KEY,
    certification_id UUID REFERENCES certifications(id),
    qualification_id UUID REFERENCES qualifications(id)
);

CREATE TABLE certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(255) NOT NULL,
  required_experience_years INT
);

CREATE TABLE qualifications (
  qualification_id INT PRIMARY KEY,
  qualification_name VARCHAR(255) NOT NULL,
  qualification_description TEXT
);

CREATE TABLE employee_certifications (
  employee_id INT,
  certification_id INT,
  date_achieved DATE,
  PRIMARY KEY (employee_id, certification_id)
);

CREATE TABLE employee_qualifications (
  employee_id INT,
  qualification_id INT,
  expiration_date DATE,
  PRIMARY KEY (employee_id, qualification_id)
);

CREATE TABLE certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(255) NOT NULL,
  required_experience INT,
  required_courses JSON
);

CREATE TABLE courses (
  course_id INT PRIMARY KEY,
  course_name VARCHAR(255) NOT NULL,
  course_duration INT,
  certification_id INT REFERENCES certifications(certification_id)
);

CREATE TABLE job_positions (
  position_id INT PRIMARY KEY,
  position_title VARCHAR(100) NOT NULL,
  minimum_experience INT,
  required_certification_id INT REFERENCES certifications(certification_id)
);

CREATE TABLE employee_certifications (
  cert_id INT PRIMARY KEY,
  employee_id INT REFERENCES employees(employee_id),
  certification_id INT REFERENCES certifications(certification_id),
  certification_date DATE
);
