tables.table_schema:
CREATE TABLE employee_benefits (
 employee_id INT PRIMARY KEY,
 is_eligible BOOLEAN,
 effective_date DATE
);

CREATE TABLE enrollment_status (
 enrollment_id INT PRIMARY KEY,
 employee_id INT,
 enroll_date DATE,
 status VARCHAR(50)
);

CREATE TABLE benefits (
    benefit_id INT PRIMARY KEY,
    benefit_name VARCHAR(255),
    cost DECIMAL(10, 2),
    coverage TEXT
);
