tables.table_schema:
CREATE TABLE employee_leave_balances (
    employee_id INT PRIMARY KEY,
    vacation_days FLOAT,
    sick_days FLOAT
);

CREATE TABLE employee_leave_balances (
  employee_id INT,
  vacation_balance INT,
  sick_balance INT
);

CREATE TABLE leave_accruals (
  employee_id INT,
  vacation_accrual_rate DECIMAL,
  sick_accrual_rate DECIMAL,
  accrual_date DATE
);

CREATE TABLE employees (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    department VARCHAR(50),
    start_date DATE
);
