tables.table_schema:
CREATE TABLE employee_benefit_costs (
  employee_id INT,
  benefits_type VARCHAR(50),
  cost DECIMAL(10, 2)
);

CREATE TABLE EmployeeBenefits (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    benefit_type VARCHAR(50),
    benefit_amount DECIMAL(10, 2)
);

CREATE TABLE FinancialPlanning (
    plan_id INT PRIMARY KEY,
    plan_name VARCHAR(50),
    planned_year INT,
    planned_amount DECIMAL(10, 2)
);

CREATE TABLE benefits (
    benefit_id INT PRIMARY KEY,
    benefit_name VARCHAR(255),
    benefit_type VARCHAR(50),
    benefit_cost DECIMAL(10, 2)
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(255),
    manager_id INT,
    location VARCHAR(100)
);

CREATE TABLE budgets (
    budget_id INT PRIMARY KEY,
    department_id INT,
    budget_amount DECIMAL(15, 2),
    budget_year INT
);

CREATE TABLE Benefits (
    benefit_id INT PRIMARY KEY,
    benefit_name VARCHAR(50),
    description TEXT,
    cost DECIMAL
);

CREATE TABLE Employee_Benefits (
    employee_id INT,
    benefit_id INT,
    CONSTRAINT fk_employee_id FOREIGN KEY (employee_id) REFERENCES Employees(employee_id),
    CONSTRAINT fk_benefit_id FOREIGN KEY (benefit_id) REFERENCES Benefits(benefit_id)
);

CREATE TABLE Departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50)
);

CREATE TABLE Budgets (
    budget_id INT PRIMARY KEY,
    department_id INT,
    total_budget DECIMAL,
    CONSTRAINT fk_department_id FOREIGN KEY (department_id) REFERENCES Departments(department_id)
);
