tables.table_schema:
CREATE TABLE recruitment_tools (
    tool_id UUID PRIMARY KEY,
    tool_name STRING,
    tool_type STRING,
    integration_method STRING
);

CREATE TABLE recruitment_tool (
  id INT PRIMARY KEY,
  tool_name VARCHAR(255) NOT NULL,
  integration_key VARCHAR(50) UNIQUE
);

CREATE TABLE candidate (
  id INT PRIMARY KEY,
  first_name VARCHAR(50) NOT NULL,
  last_name VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE
);

CREATE TABLE background_check (
  id INT PRIMARY KEY,
  candidate_id INT REFERENCES candidate(id),
  background_status VARCHAR(50) NOT NULL
);

CREATE TABLE onboarding_process (
  id INT PRIMARY KEY,
  candidate_id INT REFERENCES candidate(id),
  start_date DATE,
  completed BOOLEAN
);
