  name VARCHAR(100),
  location VARCHAR(255)
);

CREATE TABLE employee_relationships (
  relationship_id INT PRIMARY KEY,
  employee_id1 INT,
  employee_id2 INT,
  relationship_type VARCHAR(50)
);

CREATE TABLE departments ( 
 department_id INT PRIMARY KEY, 
