tables.table_schema:
CREATE TABLE EmployeeRoles (
    employee_id INT PRIMARY KEY,
    role_name VARCHAR(50),
    responsibilities TEXT
);

CREATE TABLE employee_roles (
    employee_id INT PRIMARY KEY,
    role_title VARCHAR(50),
    department VARCHAR(50),
    salary DECIMAL
);

CREATE TABLE employee_responsibilities (
    employee_id INT PRIMARY KEY,
    responsibility_title VARCHAR(50),
    priority INT,
    deadline DATE
);

CREATE TABLE employee_reviews (
    employee_id INT PRIMARY KEY,
    review_date DATE,
    performance_rating INT,
    reviewer_name VARCHAR(50)
);

CREATE TABLE EmployeeRoles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL,
    department VARCHAR(50) NOT NULL
);

CREATE TABLE EmployeeResponsibilities (
    responsibility_id INT PRIMARY KEY,
    responsibility_description TEXT,
    role_id INT,
    FOREIGN KEY (role_id) REFERENCES EmployeeRoles(role_id)
);

CREATE TABLE roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50),
    responsibilities TEXT
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50)
);
