tables.table_schema:
CREATE TABLE exit_interviews (
  employee_id int,
  exit_reason varchar(255),
  exit_date date
);

CREATE TABLE exit_interviews (
    employee_id INT PRIMARY KEY,
    exit_date DATE,
    reason TEXT
);

CREATE TABLE turnover_reasons (
    reason_id INT PRIMARY KEY,
    reason_description TEXT
);

CREATE TABLE EmployeeExitInterviews ( 
    employee_id INT PRIMARY KEY, 
    exit_reason TEXT, 
    exit_date DATE 
);

CREATE TABLE EmployeeDetails ( 
    employee_id INT PRIMARY KEY, 
    first_name TEXT, 
    last_name TEXT, 
    hire_date DATE 
);

CREATE TABLE ExitInterviewQuestions ( 
    question_id INT PRIMARY KEY, 
    question_text TEXT 
);

CREATE TABLE EmployeeDepartments ( 
    employee_id INT, 
    department_id INT, 
    PRIMARY KEY (employee_id, department_id), 
    FOREIGN KEY (employee_id) REFERENCES EmployeeDetails(employee_id) 
);
