tables.table_schema:
CREATE TABLE RobotConfigurations (
    robot_id INT PRIMARY KEY,
    robot_name VARCHAR(50),
    configuration JSONB
);
CREATE TABLE RobotStatusUpdates (
    status_id SERIAL PRIMARY KEY,
    robot_id INT,
    status VARCHAR(50),
    updated_at TIMESTAMP
);

CREATE TABLE robot_statuses (
  id SERIAL PRIMARY KEY,
  robot_id INT REFERENCES robot_configurations(id),
  status TEXT,
  last_updated TIMESTAMP
);
