tables.table_schema:
CREATE TABLE RobotStates (
    robot_id UUID PRIMARY KEY,
    location VARCHAR(100),
    battery_level DECIMAL,
    status VARCHAR(50)
);
CREATE TABLE RobotLogs (
    log_id SERIAL PRIMARY KEY,
    robot_id UUID,
    timestamp TIMESTAMP,
    event_description TEXT
);

CREATE TABLE Robot (
  robot_id INT PRIMARY KEY,
  robot_name VARCHAR(50) NOT NULL,
  battery_level DECIMAL
);

CREATE TABLE Waypoint (
  waypoint_id INT PRIMARY KEY,
  waypoint_name VARCHAR(50) NOT NULL,
  location_x DECIMAL,
  location_y DECIMAL
);

CREATE TABLE Robot (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  model VARCHAR(50),
  battery_capacity INT
);
