tables.table_schema:
CREATE TABLE FacialRecognitionData (
  person_id INT PRIMARY KEY,
  face_encoding BYTEA
);

CREATE TABLE facial_recognition_data (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50) NOT NULL,
    face_encoding BYTEA
);

CREATE TABLE face_matches (
    match_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    confidence_score FLOAT,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE FaceImage (
    face_id INT PRIMARY KEY,
    image_data BYTEA,
    image_name VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE face_templates (
    template_id INT PRIMARY KEY,
    image_url TEXT NOT NULL,
    face_encoding BYTEA NOT NULL
);

CREATE TABLE face_matches (
    match_id INT PRIMARY KEY,
    template_id INT REFERENCES face_templates(template_id),
    match_score FLOAT
);

CREATE TABLE face_users (
    user_id INT PRIMARY KEY,
    username TEXT NOT NULL,
    password TEXT NOT NULL
);

CREATE TABLE face_logs (
    log_id INT PRIMARY KEY,
    user_id INT REFERENCES face_users(user_id),
    log_time TIMESTAMP
);
