tables.table_schema:
CREATE TABLE feature_vectors (
    image_id INT PRIMARY KEY,
    feature_vector FLOAT ARRAY
);

CREATE TABLE image_clusters (
    cluster_id INT PRIMARY KEY,
    cluster_name VARCHAR(50),
    image_ids INT ARRAY
);

CREATE TABLE ImageFeatureVectors (
  image_id UUID PRIMARY KEY,
  feature_vector BYTEA
);

CREATE TABLE ImageSimilaritySearch (
  image_id UUID PRIMARY KEY,
  similar_images UUID[],
  FOREIGN KEY (image_id) REFERENCES ImageFeatureVectors(image_id)
);

CREATE TABLE ImageClustering (
  cluster_id UUID PRIMARY KEY,
  image_ids UUID[]
);

CREATE TABLE ImageFeatureVectors (
    id UUID PRIMARY KEY,
    image_id UUID,
    feature_vector BYTEA
);

CREATE TABLE ImageMetadata (
    image_id UUID PRIMARY KEY,
    image_name VARCHAR(255),
    image_path VARCHAR(255),
    upload_timestamp TIMESTAMP
);

CREATE TABLE ImageSimilarityPairs (
    pair_id UUID PRIMARY KEY,
    image_id_1 UUID,
    image_id_2 UUID,
    similarity_score FLOAT
);

CREATE TABLE ImageClusters (
    cluster_id UUID PRIMARY KEY,
    cluster_name VARCHAR(255),
    cluster_center_id UUID
);

CREATE TABLE ImageFeatureVectors (
    image_id UUID PRIMARY KEY,
    feature_vector BYTEA 
);

CREATE TABLE SimilaritySearchResults (
    result_id UUID PRIMARY KEY,
    query_image_id UUID,
    similar_image_id UUID,
    similarity_score FLOAT 
);

CREATE TABLE ClusterAssignments (
    cluster_id UUID PRIMARY KEY,
    image_id UUID 
);

CREATE TABLE ClusteringAlgorithmParams (
    param_id UUID PRIMARY KEY,
    algorithm_name TEXT,
    parameter_name TEXT,
    parameter_value TEXT 
);

CREATE TABLE ImageMetaData (
    image_id UUID PRIMARY KEY,
    image_name TEXT,
    image_url TEXT 
);
