tables.table_schema:
CREATE TABLE images (
  image_id UUID PRIMARY KEY,
  image_url VARCHAR(255) NOT NULL
);

CREATE TABLE annotations (
  annotation_id UUID PRIMARY KEY,
  image_id UUID NOT NULL,
  annotation_text TEXT
);

CREATE TABLE image_annotations (
    image_id UUID PRIMARY KEY,
    object_name VARCHAR(255),
    annotation_text TEXT
);

CREATE TABLE object_categories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(255)
);

CREATE TABLE image_object_mapping (
    mapping_id UUID PRIMARY KEY,
    image_id UUID REFERENCES image_annotations(image_id),
    object_id UUID REFERENCES object_categories(category_id)
);

CREATE TABLE image_annotations (
    image_id UUID PRIMARY KEY,
    annotation TEXT
);

CREATE TABLE annotations_bounding_boxes (
    annotation_id UUID,
    bounding_box_id UUID
);

CREATE TABLE object_classes (
    class_id UUID PRIMARY KEY,
    class_name TEXT
);

CREATE TABLE annotation_classes (
    annotation_id UUID,
    class_id UUID
);
