tables.table_schema:
CREATE TABLE camera_calibration_parameters (
    id SERIAL PRIMARY KEY,
    focal_length REAL,
    distortion_coefficients VARCHAR(255)
);
CREATE TABLE image_samples (
    sample_id SERIAL PRIMARY KEY,
    image_path VARCHAR(255),
    calibration_id INT,
    FOREIGN KEY (calibration_id) REFERENCES camera_calibration_params(id)
);

CREATE TABLE ImageDistortionCorrection (
    id INT PRIMARY KEY,
    image_id INT,
    distortion_correction_matrix TEXT,
    distortion_type VARCHAR(50)
);

CREATE TABLE CalibratedImages (
    id INT PRIMARY KEY,
    image_id INT,
    calibration_date DATE,
    calibration_quality VARCHAR(50)
);

CREATE TABLE distortion_correction (
    correction_id INT PRIMARY KEY,
    image_id INT,
    corrected_image BLOB,
    FOREIGN KEY (image_id) REFERENCES images(image_id)
);

CREATE TABLE ImageMetadata (
    id INT PRIMARY KEY,
    image_id INT,
    resolution VARCHAR(20),
    timestamp TIMESTAMP
);

CREATE TABLE CameraCalibrationData (
    id INT PRIMARY KEY,
    camera_id INT,
    calibration_date DATE,
    calibration_notes TEXT
);

CREATE TABLE ImageDistortionCorrection (
    id INT PRIMARY KEY,
    image_id INT,
    corrected_image BLOB,
    correction_timestamp TIMESTAMP
);
