tables.table_schema:
CREATE TABLE image_segmentation_masks (
    id INT PRIMARY KEY,
    image_name VARCHAR(255) NOT NULL,
    mask_data BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE ImageMasks (
    mask_id SERIAL PRIMARY KEY,
    image_id INT NOT NULL,
    mask_data BYTEA NOT NULL
);

CREATE TABLE Images (
    image_id SERIAL PRIMARY KEY,
    image_name VARCHAR(255) NOT NULL,
    image_data BYTEA NOT NULL
);

CREATE TABLE image_masks (
    mask_id UUID PRIMARY KEY,
    image_id UUID REFERENCES images(image_id),
    mask_data BYTEA
);

CREATE TABLE images (
    image_id UUID PRIMARY KEY,
    image_name VARCHAR(255),
    image_data BYTEA
);

CREATE TABLE segmentation_tasks (
    task_id UUID PRIMARY KEY,
    image_id UUID REFERENCES images(image_id),
    num_classes INTEGER,
    task_description TEXT
);

CREATE TABLE image_masks (
  image_id UUID,
  mask_data BYTEA
);

CREATE TABLE image_labels (
  image_id UUID,
  label_data VARCHAR
);

CREATE TABLE image_details (
  image_id UUID,
  file_name VARCHAR,
  image_path VARCHAR
);

CREATE TABLE image_metadata (
  image_id UUID,
  capture_date TIMESTAMP,
  location VARCHAR
);
