tables.table_schema:
CREATE TABLE ImageMatrices (
    id SERIAL PRIMARY KEY,
    matrix_name VARCHAR(50) NOT NULL,
    transformation_matrix TEXT NOT NULL
);

CREATE TABLE image_matrices (
    id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    matrix_name VARCHAR NOT NULL,
    matrix_values JSONB NOT NULL
);

CREATE TABLE image_warping (
    id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    warp_type VARCHAR NOT NULL,
    warp_parameters JSONB NOT NULL
);

CREATE TABLE perspective_correction (
    id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    correction_type VARCHAR NOT NULL,
    correction_values JSONB NOT NULL
);

CREATE TABLE ImageMatrices (
    id INT PRIMARY KEY,
    matrix_name VARCHAR(50) NOT NULL,
    matrix_data JSON NOT NULL
);

CREATE TABLE TransformationLogs (
    log_id INT PRIMARY KEY,
    transformation_type VARCHAR(50) NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    user_id INT NOT NULL
);

CREATE TABLE image_transformations (
    id SERIAL PRIMARY KEY,
    image_id INT NOT NULL,
    transformation_matrix TEXT NOT NULL
);

CREATE TABLE image_warps (
    id SERIAL PRIMARY KEY,
    image_id INT NOT NULL,
    warp_matrix TEXT NOT NULL
);

CREATE TABLE perspective_transformations (
    id SERIAL PRIMARY KEY,
    image_id INT NOT NULL,
    perspective_matrix TEXT NOT NULL
);

CREATE TABLE perspective_corrections (
    id SERIAL PRIMARY KEY,
    image_id INT NOT NULL,
    correction_matrix TEXT NOT NULL
);

CREATE TABLE image_metadata (
    id SERIAL PRIMARY KEY,
    image_name TEXT NOT NULL,
    image_path TEXT NOT NULL
);
