tables.table_schema:
CREATE TABLE word_frequency (
    document_id INT PRIMARY KEY,
    word TEXT,
    frequency INT
);

CREATE TABLE TextDocuments (
    document_id INT PRIMARY KEY,
    document_text TEXT
);

CREATE TABLE WordFrequency (
    word_id SERIAL PRIMARY KEY,
    word_text TEXT,
    document_id INT,
    frequency INT,
    FOREIGN KEY (document_id) REFERENCES TextDocuments(document_id)
);

CREATE TABLE documents (
  document_id INT PRIMARY KEY,
  document_text TEXT
);

CREATE TABLE words (
  word_id INT PRIMARY KEY,
  word_text TEXT
);

CREATE TABLE documents (
    id SERIAL PRIMARY KEY,
    title TEXT,
    content TEXT
);

CREATE TABLE authors (
    id SERIAL PRIMARY KEY,
    name TEXT
);

CREATE TABLE document_authors (
    document_id INT,
    author_id INT,
    FOREIGN KEY (document_id) REFERENCES documents(id),
    FOREIGN KEY (author_id) REFERENCES authors(id)
);

CREATE TABLE word_document_mapping (
    word_id INT,
    document_id INT,
    FOREIGN KEY (word_id) REFERENCES words(id),
    FOREIGN KEY (document_id) REFERENCES documents(id)
);
