tables.table_schema:
CREATE TABLE stock_analysis (
  stock_symbol VARCHAR,
  analysis_date DATE,
  analysis_result TEXT
);

CREATE TABLE Stocks (
    symbol TEXT PRIMARY KEY,
    company_name TEXT,
    sector TEXT,
    exchange TEXT
);

CREATE TABLE DividendHistory (
    id UUID PRIMARY KEY,
    symbol TEXT,
    ex_dividend_date DATE,
    dividend_amount NUMERIC
);
