tables.table_schema:
CREATE TABLE audio_waveform_data (
  id INT PRIMARY KEY,
  waveform_data BYTEA
);

CREATE TABLE speech_recognition_models (
  model_id INT PRIMARY KEY,
  model_name TEXT,
  model_accuracy FLOAT
);

CREATE TABLE audio_waveform_data (
  id INT PRIMARY KEY,
  waveform BLOB,
  duration FLOAT,
  sample_rate INT
);

CREATE TABLE training_data (
  data_id INT PRIMARY KEY,
  audio_id INT,
  transcript TEXT
);

CREATE TABLE model_parameters (
  parameter_id INT PRIMARY KEY,
  model_id INT,
  parameters JSON
);

CREATE TABLE AudioFiles (
    id INT PRIMARY KEY,
    file_name VARCHAR(255),
    waveform_data BYTEA
);

CREATE TABLE Transcriptions (
    id INT PRIMARY KEY,
    file_id INT,
    transcription_text TEXT,
    FOREIGN KEY (file_id) REFERENCES AudioFiles(id)
);

CREATE TABLE Speakers (
    id INT PRIMARY KEY,
    speaker_name VARCHAR(255)
);

CREATE TABLE audio_waveform_data (
    id INT PRIMARY KEY,
    waveform_data BYTEA,
    transcription TEXT
);

CREATE TABLE training_data (
    id INT PRIMARY KEY,
    audio_id INT,
    feature_vector JSONB
);

CREATE TABLE speech_models (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    model_data BYTEA
);

CREATE TABLE model_metadata (
    id INT PRIMARY KEY,
    model_id INT,
    description TEXT
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);
