tables.table_schema:
CREATE TABLE tagging_text_data (
  id SERIAL PRIMARY KEY,
  text_data TEXT,
  part_of_speech TEXT
);

CREATE TABLE text_data (
  id INT PRIMARY KEY,
  text VARCHAR(255),
  part_of_speech VARCHAR(50)
);

CREATE TABLE pos_tags (
  tag_id INT PRIMARY KEY,
  tag VARCHAR(50)
);

CREATE TABLE text_data_tags (
    id SERIAL PRIMARY KEY,
    text_data VARCHAR(255),
    part_of_speech VARCHAR(50)
);

CREATE TABLE tags (
    id SERIAL PRIMARY KEY,
    tag_name VARCHAR(50) UNIQUE
);

CREATE TABLE tag_data (
  id SERIAL PRIMARY KEY,
  text_data TEXT NOT NULL,
  pos_info TEXT
);

CREATE TABLE nouns (
  id SERIAL PRIMARY KEY,
  noun_word TEXT NOT NULL
);

CREATE TABLE verbs (
  id SERIAL PRIMARY KEY,
  verb_word TEXT NOT NULL
);

CREATE TABLE adjectives (
  id SERIAL PRIMARY KEY,
  adjective_word TEXT NOT NULL
);

CREATE TABLE Text_Data (
    text_id INT PRIMARY KEY,
    text_content TEXT
);

CREATE TABLE Part_of_Speech (
    pos_id INT PRIMARY KEY,
    pos_tag VARCHAR(50),
    pos_description TEXT
);

CREATE TABLE User_Tags (
    user_id INT PRIMARY KEY,
    tag_id INT,
    tag_timestamp TIMESTAMP,
    FOREIGN KEY (tag_id) REFERENCES Tagged_Text_Data(tag_id)
);

CREATE TABLE User_Profile (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);
