tables.table_schema:
CREATE TABLE cameras (
    camera_id INT PRIMARY KEY,
    capture_time TIMESTAMP,
    resolution VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE cameras (
    camera_id INT PRIMARY KEY,
    location VARCHAR(50),
    resolution VARCHAR(20),
    framerate INT
);

CREATE TABLE lidar (
    lidar_id INT PRIMARY KEY,
    location VARCHAR(50),
    scan_range INT
);

CREATE TABLE radar (
    radar_id INT PRIMARY KEY,
    location VARCHAR(50),
    detection_range INT,
    accuracy VARCHAR(20)
);

CREATE TABLE camera_data (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    image_path VARCHAR(255) NOT NULL
);

CREATE TABLE lidar_data (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    distance FLOAT NOT NULL,
    angle FLOAT NOT NULL
);

CREATE TABLE radar_data (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    velocity FLOAT NOT NULL,
    range FLOAT NOT NULL
);

CREATE TABLE sensor_metadata (
    id INT PRIMARY KEY,
    sensor_type VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    installation_date DATE NOT NULL
);

CREATE TABLE Cameras (
 id INT PRIMARY KEY,
 camera_name VARCHAR(50),
 location VARCHAR(100),
 resolution VARCHAR(20)
);

CREATE TABLE LiDAR (
 id INT PRIMARY KEY,
 lidar_name VARCHAR(50),
 location VARCHAR(100),
 max_range FLOAT
);

CREATE TABLE Radar (
 id INT PRIMARY KEY,
 radar_name VARCHAR(50),
 location VARCHAR(100),
 detection_range FLOAT
);

CREATE TABLE AnalysisData (
 id INT PRIMARY KEY,
 timestamp TIMESTAMP,
 analysis_type VARCHAR(50),
 result VARCHAR(100)
);
