tables.table_schema:
CREATE TABLE historical_traffic_data (
	route_id INT,
	date DATE,
	time TIME,
	traffic_volume INT
);

CREATE TABLE route_mapping (
	route_id INT,
	start_location VARCHAR,
	end_location VARCHAR
);

CREATE TABLE route_planning (
	route_id INT,
	start_location VARCHAR,
	end_location VARCHAR,
	recommended_speed INT
);

CREATE TABLE weather_data (
	date DATE,
	time TIME,
	temperature FLOAT,
	rain_intensity INT
);
