CREATE TABLE weather_forecasts (
	id INT PRIMARY KEY,
	date DATE,
	temperature INT,
	conditions VARCHAR(255)
);

CREATE TABLE road_conditions (
	id INT PRIMARY KEY,
	road_name VARCHAR(255),
	traffic_flow VARCHAR(255),
	road_condition VARCHAR(255)
);

CREATE TABLE adaptive_strategies (
	id INT PRIMARY KEY,
	strategy_name VARCHAR(255),
	speed_limit INT,
	distance_to_vehicle INT
);

