tables.table_schema:
CREATE TABLE simulations (
    simulation_id INT PRIMARY KEY,
    simulation_name VARCHAR(255),
    simulation_date DATE
);
CREATE TABLE test_scenarios (
    scenario_id INT PRIMARY KEY,
    scenario_name VARCHAR(255),
    test_type VARCHAR(50)
);

CREATE TABLE simulations (
    id INT PRIMARY KEY,
    simulation_name STRING,
    description STRING,
    simulation_date DATE
);

CREATE TABLE testing_scenarios (
    id INT PRIMARY KEY,
    scenario_name STRING,
    scenario_description STRING,
    testing_date DATE
);

CREATE TABLE autonomous_driving_algorithms (
    id INT PRIMARY KEY,
    algorithm_name STRING,
    algorithm_description STRING,
    last_updated TIMESTAMP
);

CREATE TABLE algorithm_parameters (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    parameter_name VARCHAR(255),
    parameter_value VARCHAR(255)
);

CREATE TABLE test_scenarios (
    scenario_id INT PRIMARY KEY,
    scenario_description TEXT,
    road_conditions VARCHAR(255),
    weather_conditions VARCHAR(255)
);

CREATE TABLE Vehicle (
    vehicle_id SERIAL PRIMARY KEY,
    vehicle_type VARCHAR(50) NOT NULL,
    manufacturer VARCHAR(100),
    year INT
);

CREATE TABLE Sensor (
    sensor_id SERIAL PRIMARY KEY,
    sensor_type VARCHAR(50) NOT NULL,
    installation_date DATE,
    vehicle_id INT REFERENCES Vehicle(vehicle_id)
);

CREATE TABLE Scenario (
    scenario_id SERIAL PRIMARY KEY,
    scenario_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE Algorithm (
    algorithm_id SERIAL PRIMARY KEY,
    algorithm_name VARCHAR(100) NOT NULL,
    description TEXT,
    accuracy DECIMAL
);
