CREATE TABLE text_data (id INT PRIMARY KEY,
               text_content TEXT
             )

CREATE TABLE text_data (
    id INT PRIMARY KEY,
    text_content TEXT
);

CREATE TABLE summaries (
    id INT PRIMARY KEY,
    text_id INT REFERENCES text_data(id),
    summary_content TEXT
);

CREATE TABLE document (
    id INT PRIMARY KEY,
    text TEXT
);

CREATE TABLE sentence (
    id INT PRIMARY KEY,
    document_id INT, 
    sentence_text TEXT, 
    FOREIGN KEY (document_id) REFERENCES document(id)
);

CREATE TABLE word (
    id INT PRIMARY KEY, 
    sentence_id INT, 
    word_text VARCHAR(255), 
    FOREIGN KEY (sentence_id) REFERENCES sentence(id)
);

CREATE TABLE summary (
    id INT PRIMARY KEY,
    document_id INT, 
    summary_text TEXT, 
    FOREIGN KEY (document_id) REFERENCES document(id)
);

CREATE TABLE text_documents (
    id INT PRIMARY KEY,
    document_text TEXT
);

CREATE TABLE sentences (
    id INT PRIMARY KEY,
    document_id INT,
    sentence_text TEXT
);

CREATE TABLE keywords (
    id INT PRIMARY KEY,
    sentence_id INT,
    keyword_text TEXT
);

CREATE TABLE summaries (
    id INT PRIMARY KEY,
    document_id INT,
    summary_text TEXT
);

CREATE TABLE evaluations (
    id INT PRIMARY KEY,
    summary_id INT,
    evaluation_score FLOAT
);
