tables.table_schema:
CREATE TABLE user_profiles (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE assistant_profiles (
    assistant_id INT PRIMARY KEY,
    assistant_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    department VARCHAR(50) NOT NULL
);

CREATE TABLE user_assistant_mapping (
    user_id INT REFERENCES user_profiles(user_id),
    assistant_id INT REFERENCES assistant_profiles(assistant_id),
    PRIMARY KEY(user_id, assistant_id)
);

CREATE TABLE user_activity_log (
    log_id INT PRIMARY KEY,
    user_id INT REFERENCES user_profiles(user_id),
    action VARCHAR(100) NOT NULL,
    timestamp TIMESTAMP
);
