tables.table_schema:
CREATE TABLE user_activity_logs (
    log_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    activity_description TEXT
);

CREATE TABLE UserHistory (
    user_id INT NOT NULL,
    action_time TIMESTAMP NOT NULL,
    action_description VARCHAR(255) NOT NULL
);

CREATE TABLE ActivityLogs (
    log_id INT NOT NULL,
    user_id INT NOT NULL,
    log_time TIMESTAMP NOT NULL,
    log_description TEXT NOT NULL
);

CREATE TABLE virtual_assistants_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE assistant_sessions (
    session_id INT PRIMARY KEY,
    user_id INT REFERENCES virtual_assistants_users(user_id),
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP
);

CREATE TABLE user_activity_logs (
    log_id INT PRIMARY KEY,
    user_id INT REFERENCES virtual_assistants_users(user_id),
    action VARCHAR(100) NOT NULL,
    log_time TIMESTAMP
);

CREATE TABLE assistant_errors (
    error_id INT PRIMARY KEY,
    session_id INT REFERENCES assistant_sessions(session_id),
    error_message TEXT NOT NULL,
    error_time TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255),
    date_joined DATE
);

CREATE TABLE assistant_activity (
    activity_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE user_history (
    history_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE assistant_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    log_text TEXT,
    log_date DATE
);

CREATE TABLE session_logs (
    session_id INT PRIMARY KEY,
    user_id INT,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);
