    id INT PRIMARY KEY,
    question TEXT NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE faq_responses (
    id INT PRIMARY KEY,
    response TEXT NOT NULL,
    question_id INT REFERENCES faq_questions(id)
);

