tables.table_schema:
CREATE TABLE UserSentimentAndFeedbackForVirtualAssistants (
    user_id INT,
    virtual_assistant_id INT,
    sentiment_score FLOAT,
    feedback_message TEXT
);

CREATE TABLE UserSentiment (
    user_id INT PRIMARY KEY,
    sentiment_score FLOAT,
    feedback TEXT
);

CREATE TABLE VirtualAssistant (
    assistant_id INT PRIMARY KEY,
    assistant_name VARCHAR(50),
    assistant_type VARCHAR(50)
);

CREATE TABLE VirtualAssistantFeedback (
    id INT PRIMARY KEY,
    user_id INT,
    feedback_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE VirtualAssistantUsage (
    id INT PRIMARY KEY,
    user_id INT,
    query_text TEXT,
    date TIMESTAMP
);

CREATE TABLE FeedbackAnalysisResults (
    id INT PRIMARY KEY,
    feedback_id INT,
    analysis_result TEXT,
    analysis_date DATE
);

CREATE TABLE VirtualAssistantFeedback (
    feedback_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    feedback_text TEXT NOT NULL,
    sentiment_score FLOAT NOT NULL
);

CREATE TABLE UserActivityLogs (
    log_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    log_description TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);
