tables.table_schema:
CREATE TABLE teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255),
    team_location VARCHAR(255)
);

CREATE TABLE WinningTeams (
    id SERIAL PRIMARY KEY,
    team_name VARCHAR(255) NOT NULL,
    score INT NOT NULL
);

CREATE TABLE LosingTeams (
    id SERIAL PRIMARY KEY,
    team_name VARCHAR(255) NOT NULL,
    score INT NOT NULL
);

CREATE TABLE MatchResults (
    id SERIAL PRIMARY KEY,
    winning_team_id INT REFERENCES WinningTeams(id),
    losing_team_id INT REFERENCES LosingTeams(id),
    game_duration INTERVAL NOT NULL
);

CREATE TABLE winning_teams (
