tables.table_schema:
CREATE TABLE player_profiles (
    player_id INT PRIMARY KEY,
    player_name VARCHAR,
    player_level INT,
    player_experience INT
);

CREATE TABLE PlayerProfiles (
  id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100) UNIQUE,
  avatar_url TEXT
);

CREATE TABLE GameCatalog (
  id INT PRIMARY KEY,
  title VARCHAR(100),
  genre VARCHAR(50),
  release_date DATE
);

CREATE TABLE PlayerProgress (
  player_id INT REFERENCES PlayerProfiles(id),
  game_id INT REFERENCES GameCatalog(id),
  current_level INT,
  last_played DATE
);

CREATE TABLE player_profiles (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    player_email VARCHAR(100),
    player_location VARCHAR(50)
);

CREATE TABLE game_levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(50),
    level_difficulty VARCHAR(20)
);
