tables.table_schema:
CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    game_preferences JSONB
);

CREATE TABLE user_behavior (
    user_id INT PRIMARY KEY,
    behavior_data JSONB
);

CREATE TABLE UserPreferences (
    user_id INT,
    preference_1 VARCHAR,
    preference_2 VARCHAR
);

CREATE TABLE UserBehavior (
    user_id INT,
    action_date DATE,
    action_type VARCHAR
);

CREATE TABLE PersonalizedExperiences (
    experience_id INT,
    user_id INT,
    experience_name VARCHAR
);

CREATE TABLE personalized_recommendations (
  recommendation_id INT PRIMARY KEY,
  user_id INT REFERENCES user_profiles(user_id),
  recommended_game_id INT REFERENCES game_preferences(game_id),
  recommendation_score FLOAT
);

CREATE TABLE virtual_reality_games (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(50) NOT NULL,
    game_genre VARCHAR(50),
    game_rating FLOAT
);

CREATE TABLE user_game_preferences (
    user_id INT,
    game_id INT,
    preference_rating FLOAT,
    PRIMARY KEY (user_id, game_id)
);
