tables.table_schema:
CREATE TABLE TeamInformation (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    coach_name VARCHAR(50),
    total_players INT
);

CREATE TABLE schedule (
    schedule_id INT PRIMARY KEY,
    date DATE NOT NULL,
    time TIME NOT NULL,
    team_id INT
);

CREATE TABLE teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50) NOT NULL,
    team_country VARCHAR(50)
);

CREATE TABLE teams (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  country VARCHAR(50),
  coach VARCHAR(50)
);

CREATE TABLE tournament_dates (date_id INT PRIMARY KEY,
    tournament_date DATE
)

CREATE TABLE tournament_times (time_id INT PRIMARY KEY,
    tournament_time TIME
)

CREATE TABLE teams (team_id INT PRIMARY KEY,
    team_name VARCHAR(50)
)
