tables.table_schema:
CREATE TABLE player_interactions (
    player_id INT,
    interaction_type VARCHAR(50),
    interaction_timestamp TIMESTAMP,
    interaction_details JSONB
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(255),
    player_level INT,
    last_login TIMESTAMP
);

CREATE TABLE player_interactions (
    interaction_id INT PRIMARY KEY,
    player_id INT,
    interaction_type VARCHAR(50),
    interaction_time TIMESTAMP,
    FOREIGN KEY (player_id) REFERENCES players(player_id)
);

CREATE TABLE PlayerInteractions (
    player_id INT,
    interaction_type VARCHAR(50),
    interaction_timestamp TIMESTAMP,
    interaction_details TEXT
);

CREATE TABLE PlayerBehaviors (
    player_id INT,
    behavior_type VARCHAR(50),
    behavior_timestamp TIMESTAMP,
    behavior_details TEXT
);

CREATE TABLE player_data (player_id INT PRIMARY KEY, player_name VARCHAR(255), level INT, score INT);
CREATE TABLE player_sessions (session_id INT PRIMARY KEY, player_id INT, session_duration INT, start_time TIMESTAMP, end_time TIMESTAMP);
CREATE TABLE player_actions (action_id INT PRIMARY KEY, player_id INT, action_type VARCHAR(50), action_description TEXT, action_time TIMESTAMP);
CREATE TABLE game_levels (level_id INT PRIMARY KEY, level_name VARCHAR(50), level_difficulty VARCHAR(20), level_duration INT);
CREATE TABLE game_scores (score_id INT PRIMARY KEY, player_id INT, level_id INT, score_value INT, score_date DATE);
