tables.table_schema:
CREATE TABLE PlayerMetrics (
  player_id INT,
  kill_death_ratio DECIMAL(5,2),
  accuracy DECIMAL(5,2),
  win_loss_ratio DECIMAL(5,2)
);

CREATE TABLE PlayerMetrics (
  player_id INT PRIMARY KEY,
  kill_death_ratio DECIMAL,
  accuracy DECIMAL,
  win_loss_ratio DECIMAL
);

CREATE TABLE MatchHistory (
  match_id INT PRIMARY KEY,
  player_id INT,
  result VARCHAR,
  time_played TIMESTAMP
);

CREATE TABLE PlayerProfiles (
  player_id INT PRIMARY KEY,
  username VARCHAR,
  level INT,
  experience INT
);

CREATE TABLE player_stats (
  player_id INT PRIMARY KEY,
  kill_death_ratio FLOAT,
  accuracy FLOAT,
  win_loss_ratio FLOAT
);

CREATE TABLE match_results (
  match_id INT PRIMARY KEY,
  player_id INT,
  result VARCHAR(20)
);

CREATE TABLE player_skills (
  player_id INT PRIMARY KEY,
  skill_type VARCHAR(50),
  skill_value FLOAT
);

CREATE TABLE player_history (
  player_id INT PRIMARY KEY,
  game_date DATE,
  game_mode VARCHAR(50),
  game_result VARCHAR(20)
);

CREATE TABLE weapon_stats (
    weapon_id INT PRIMARY KEY,
    weapon_name VARCHAR(100),
    damage_per_shot INT,
    accuracy_penalty DECIMAL
);

CREATE TABLE player_ranking (
    player_id INT PRIMARY KEY,
    rank_position INT,
    rank_date DATE,
    FOREIGN KEY (player_id) REFERENCES player_stats(player_id)
);
