tables.table_schema:
CREATE TABLE PlayerFeedback (
    feedback_id INT PRIMARY KEY,
    player_id INT,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE PlayerFeedback (
  feedback_id INT PRIMARY KEY,
  player_id INT,
  feedback_text TEXT,
  feedback_date TIMESTAMP
);

CREATE TABLE Ratings (
  rating_id INT PRIMARY KEY,
  player_id INT,
  rating_value INT,
  rating_date TIMESTAMP
);

CREATE TABLE player_feedback (
    id SERIAL PRIMARY KEY,
    player_id INT NOT NULL,
    feedback_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE game_ratings (
    id SERIAL PRIMARY KEY,
    game_id INT NOT NULL,
    rating INT,
    timestamp TIMESTAMP
);

CREATE TABLE community_engagement (
    id SERIAL PRIMARY KEY,
    activity_type VARCHAR(255),
    activity_data JSONB,
    timestamp TIMESTAMP
);

CREATE TABLE GameImprovements (
    improvement_id UUID PRIMARY KEY,
    improvement_text TEXT,
    improvement_date TIMESTAMP
);
