tables.table_schema:
CREATE TABLE game_configurations (
	id SERIAL PRIMARY KEY,
	difficulty_level VARCHAR(50) NOT NULL,
	game_mode VARCHAR(50) NOT NULL,
	control_options VARCHAR(50) NOT NULL
);

CREATE TABLE difficulty_levels (
   id SERIAL PRIMARY KEY,
   level_name VARCHAR(50) NOT NULL
);

CREATE TABLE game_modes (
   id SERIAL PRIMARY KEY,
   mode_name VARCHAR(50) NOT NULL,
   description TEXT
);

CREATE TABLE difficulty_levels (
    id INT PRIMARY KEY,
    difficulty_name TEXT
);

CREATE TABLE game_modes (
    id INT PRIMARY KEY,
    mode_name TEXT
);

CREATE TABLE control_options (
    id INT PRIMARY KEY,
    option_name TEXT
);

CREATE TABLE difficulty_levels (
  level_id INT PRIMARY KEY,
  level_name STRING,
  difficulty_value INT
);

CREATE TABLE game_modes (
  mode_id INT PRIMARY KEY,
  mode_name STRING,
  description STRING
);

CREATE TABLE control_options (
  option_id INT PRIMARY KEY,
  option_name STRING,
  control_type STRING
);

CREATE TABLE game_settings (
  setting_id INT PRIMARY KEY,
  setting_name STRING,
  setting_value STRING
);

CREATE TABLE DifficultyLevels (
  id INT PRIMARY KEY,
  level_name VARCHAR(50) NOT NULL,
  level_description TEXT
);

CREATE TABLE GameModes (
  id INT PRIMARY KEY,
  mode_name VARCHAR(50) NOT NULL,
  mode_description TEXT
);

CREATE TABLE ControlOptions (
  id INT PRIMARY KEY,
  option_name VARCHAR(50) NOT NULL,
  option_description TEXT,
  option_value INT
);

CREATE TABLE PlayerStatistics (
  player_id INT PRIMARY KEY,
  games_played INT NOT NULL,
  highest_score INT,
  latest_play_date DATE
);

CREATE TABLE PlayerSettings (
  player_id INT PRIMARY KEY,
  preferred_difficulty INT,
  favorite_mode INT,
  control_option INT
);
