tables.table_schema:
CREATE TABLE achievements (
    achievement_id INT PRIMARY KEY,
    player_id INT,
    achievement_name VARCHAR(255),
    achieved_date TIMESTAMP
);

CREATE TABLE high_scores (
    score_id INT PRIMARY KEY,
    player_id INT,
    game_mode VARCHAR(50),
    score INT
);

CREATE TABLE PlayerAchievements (
  player_id INT,
  achievement_id INT,
  timestamp TIMESTAMP
);

CREATE TABLE PlayerScores (
  player_id INT,
  game_id INT,
  score INT,
  timestamp TIMESTAMP
);

CREATE TABLE Leaderboard (
  game_id INT,
  player_id INT,
  score INT,
  rank INT
);

CREATE TABLE players (
    player_id UUID PRIMARY KEY,
    player_name STRING NOT NULL,
    email STRING UNIQUE,
    registration_date TIMESTAMP
);

CREATE TABLE achievements (
    achievement_id SERIAL PRIMARY KEY,
    player_id UUID REFERENCES players(player_id),
    achievement_name STRING NOT NULL,
    achievement_date TIMESTAMP
);

CREATE TABLE leaderboards (
    leaderboard_id SERIAL PRIMARY KEY,
    game_id STRING REFERENCES high_scores(game_id),
    leaderboard_name STRING NOT NULL,
    leaderboard_date TIMESTAMP
);

CREATE TABLE player (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(255) NOT NULL,
    join_date TIMESTAMP
);

CREATE TABLE leaderboard (
    leaderboard_id UUID PRIMARY KEY,
    leaderboard_name VARCHAR(255) NOT NULL,
    start_date TIMESTAMP,
    end_date TIMESTAMP
);
