tables.table_schema:
CREATE TABLE game_progress (
    player_id INT PRIMARY KEY,
    save_state BYTEA,
    last_saved_at TIMESTAMP
);

CREATE TABLE game_progress (
    player_id INT PRIMARY KEY,
    game_id INT,
    progress_data JSONB
);

CREATE TABLE save_states (
    player_id INT,
    save_id UUID PRIMARY KEY,
    saved_data JSONB
);

CREATE TABLE player (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    level INT,
    progress FLOAT
);

CREATE TABLE game (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(50),
    genre VARCHAR(50),
    total_players INT
);

CREATE TABLE player (
    player_id UUID PRIMARY KEY,
    player_name STRING,
    game_progress INT,
    save_state BYTEA
);

CREATE TABLE game (
    game_id UUID PRIMARY KEY,
    game_name STRING,
    release_date DATE,
    genre STRING
);

CREATE TABLE player (
    id INT PRIMARY KEY,
    player_name VARCHAR(50) NOT NULL,
    level INT,
    coins INT
);

CREATE TABLE game (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(50) NOT NULL,
    release_date DATE,
    platform VARCHAR(50)
);

CREATE TABLE player_inventory (
    player_id INT,
    item_name VARCHAR(50),
    quantity INT,
    PRIMARY KEY (player_id, item_name)
);

CREATE TABLE player_statistics (
    player_id INT PRIMARY KEY,
    total_playtime INT,
    highest_level INT,
    coins_collected INT
);
